function dropzoneLoad() {
    Dropzone.options.uploadMedia = {
        maxFiles: 1,
        paramName: "file",
        maxFilesize: 20,
        uploadMultiple: false,
        acceptedFiles: ".jpg, .jpeg, .png, .gif, .webp, .svg",
        init: function() {
            this.on("success", function(file, response) {
                loadMedia();
            })
        }
    };
}
$('#mediaUpload').on('shown.bs.modal', function() {
    dropzoneLoad();
});
